import React, {FC, useState, Component} from 'react';
import {Box, Text, Flex, Button} from '@chakra-ui/react';
import {rankingContract} from '../contracts';
import Web3 from 'web3';
import Ranking from "../components/Ranking";
import {Socket,io} from 'socket.io-client';
import {Buffer} from 'buffer';
//FC for functional component

class RankingPage extends React.Component{

    constructor(props : any) {
        super(props);

    }

    state={Name : ''};
    onSubmit = async () => {
        try{
            const returnVal = await rankingContract.methods.solvedProblem(this.state.Name).send({from: "0xC30dd9DfaB1fb35197A67611f8b3186f6934dE31"});
            console.log(returnVal);
        }
        catch(error) {
            console.error(error);
        }
    }
    onGetQuizClicked = () => {


    }

    handleChange = (event: any) => {
        this.setState({Name: event.target.value});
    }

    render () {
        return <Flex w="full" h="10vh" justifyContent="center" alignItems="center" direction="column">

            <Text fontSize="10vh"> LeaderBoard</Text>
            <Button size="m" colorScheme="blue" onClick={this.onGetQuizClicked}>
                    Refresh
            </Button>

        </Flex>;
    }
}






